import './Login.css';
import React, {Component} from 'react';
import userLogo from '../Assets/user.png'
import passLogo from '../Assets/pass.png'
import Footer from "../HomescreenComp/footer"
import { useNavigate } from "react-router-dom";
import RequestHandle from '../RequestHelpers/RequestHandle';

//<Link to = "/logged/home" className = "create-link">Login</Link>

import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link
  } from "react-router-dom";

class Login extends Component{

    constructor(props){
        super(props);

        this.state = {
            current_email: "",
            current_pass: "",
            current_user: []
        }

        this.setPass = this.setPass.bind(this);
        this.setUser = this.setUser.bind(this);
        this.handleLogin = this.handleLogin.bind(this);
    }

    setUser(item){
        this.setState({current_email: item.target.value});
        this.forceUpdate();
    }

    setPass(item){
        this.setState({current_pass: item.target.value});
        this.forceUpdate();
    }

    handleLogin(license, email){
        var request = "https://pirfusix-solutions.herokuapp.com/login/" + license + "/" + email
        alert(request)
        RequestHandle.getLoginInfo(license, email).then((response2) => {
            var temp2 = []
            temp2 = response2.data;
            this.setState({current_user: temp2})
            alert(this.state.current_user)
        })
        .catch(function (ex) {
            console.log("Response parsing failed, Error: ", ex)
        });;
    }

    render(){
        return (
            <div className = "login-back">
                <header className = "top">pIRfusiX Solutions</header>
                <div id = "whitespace"></div>
                    <form className = "logbox">
                        <h1 className = "login">Login</h1>
                        <div className='user-div'>
                            <img src = {userLogo} className = "user-logo"></img>
                            <input type = "text" onChange = {(event) => this.setUser(event)} className = "username" placeholder = "EMAIL"></input>
                        </div>
                        <div className='pass-div'>
                            <img src = {passLogo} className = "pass-logo"></img>
                            <input type = "text" onChange = {(event) => this.setPass(event)} className = "password" placeholder = "LICENSE NUMBER"></input>
                        </div>
                        <button className = "confirm" onClick = {() => this.handleLogin(this.state.current_pass, this.state.current_email)}>Login</button>
                        <div className = "login-footer">
                            <div className = "dha">Don't have an account?</div>
                            <Link to = "/NewAccount" className = "create-link">Create</Link>
                        </div>
                    </form>
                <h1 className = "divider"></h1>
                <Footer/>
            </div>
        )
    }
}

export default Login;
